import React from 'react';
import { Link } from 'react-router-dom';
import { Label, Icon } from 'semantic-ui-react';
import Image from '../../../../../shared/components/Image';
import LectureInstructor from '../../../viewModel/LectureOverview/LectureInstructor';
import {
  parsePolyglotString,
  PolyglotString,
} from 'shared/viewmodel/PolyglotString';
import { getDefaultLang } from 'lecture/model/LangSupport';
import { PolyglotText } from 'shared/ui/logic/PolyglotText';

interface LectureClassroomInstructorViewProps {
  lectureInstructor: LectureInstructor;
}

function Represent() {
  return (
    <img
      src={parsePolyglotString(REPRESENT_ICON_POLYGLOT_URL)}
      className="p-label"
      alt="강사 이미지"
    />
  );
}

export function LectureClassroomInstructorView(
  props: LectureClassroomInstructorViewProps
) {
  const { lectureInstructor } = props;

  return (
    <div className="ov-paragraph teacher-info">
      <div className="title">
        <h3 className="title-style">
          <Label className="onlytext bold size24">
            <Icon className="host" />
            <span>
              {/*Tag*/}
              <PolyglotText
                defaultString="강사정보"
                id="cube-Contents-강사정보"
              />
            </span>
          </Label>
        </h3>
      </div>
      <div className="scrolling course-profile">
        {lectureInstructor &&
          lectureInstructor.instructors &&
          lectureInstructor.instructors.map(
            (
              { instructorId, representative, instructorWithIdentity },
              index
            ) => (
              <Link
                className="ui profile tool-tip"
                to={`/expert/instructor/${instructorId}/Introduce`}
              >
                {representative === true && <Represent />}
                <div className="pic s80">
                  {instructorWithIdentity?.instructor?.photoFilePath && (
                    <Image
                      alt="프로필사진"
                      className="ui image"
                      src={instructorWithIdentity?.instructor.photoFilePath}
                    />
                  )}
                </div>
                <i>
                  <span className="tip-name">
                    {parsePolyglotString(
                      instructorWithIdentity?.instructor?.name,
                      getDefaultLang(
                        instructorWithIdentity?.instructor?.langSupports
                      )
                    )}
                  </span>
                  <a className="tip-id">
                    {instructorWithIdentity?.instructor?.internal
                      ? parsePolyglotString(
                          instructorWithIdentity?.userIdentity?.departmentName,
                          getDefaultLang(
                            instructorWithIdentity?.instructor?.langSupports
                          )
                        )
                      : parsePolyglotString(
                          instructorWithIdentity?.instructor?.organization,
                          getDefaultLang(
                            instructorWithIdentity?.instructor?.langSupports
                          )
                        )}
                  </a>
                </i>
              </Link>
            )
          )}
      </div>
    </div>
  );
}

const REPRESENT_ICON_POLYGLOT_URL: PolyglotString = {
  ko: 'data:image/svg+xml;charset=utf-8;base64,',
  en: 'data:image/png;base64,',
  zh: 'data:image/png;base64,',
};

// ko image
const REPRESENT_IMAGE =
  'data:image/svg+xml;charset=utf-8;base64,';
